import React from "react";
import Image from "next/image";
import Textbox from "./Textbox";
import { Page } from "./page";
import { OcrBlock } from "page";

const PageContainer = ({
  page,
  preloads,
  getImageUri,
}: {
  page: Page;
  preloads: Page[];
  getImageUri: (id: number) => string;
}) => {
  return page ? (
    <div className="inline-flex relative page">
      <div
        className="pageContainer"
        style={{
          width: page.ocr.img_width,
          height: page.ocr.img_height,
          backgroundImage: getImageUri(page.id),
          fontFamily: "Noto Sans JP",
        }}
      >
        <Image
          key={getImageUri(page.id)}
          src={getImageUri(page.id)}
          width={page.ocr.img_width}
          height={page.ocr.img_height}
          alt={`Page ${page.number}`}
          unoptimized={true}
          priority={true}
          placeholder="data:image/svg+xml;base64,"
        />
        {preloads.map((nextPage) => (
          <Image
            key={getImageUri(nextPage.id)}
            src={getImageUri(nextPage.id)}
            width={nextPage.ocr.img_width}
            height={nextPage.ocr.img_height}
            alt={`Page ${nextPage.number}`}
            unoptimized={true}
            priority={true}
            style={{ display: "none" }}
            placeholder="data:image/svg+xml;base64,"
          />
        ))}
        {page.ocr.blocks.map((block: OcrBlock) => (
          <Textbox
            key={block.box.join(",")}
            box={block.box}
            fontSize={block.font_size}
            vertical={block.vertical}
            lines={block.lines}
          />
        ))}
      </div>
    </div>
  ) : (
    // eslint-disable-next-line @next/next/no-img-element
    <img
      src="data:image/svg+xml;base64,"
      width={1000}
      height="100%"
      alt="Loading"
    />
  );
};

export default PageContainer;
